function param(Name){
	 var Params = location.search.substring(1).split("&");
	 var variable = "";
	 for (var i = 0; i < Params.length; i++){
		 if(Params[i].split("=")[0] == Name){
			 if (Params[i].split("=").length > 1){
			 variable = Params[i].split("=")[1];
			 variable = decodeURIComponent(variable);
			 return variable;
			 }
		 }
	 }
	 return "";
}//Функция получения данных из предыдущей странцы для запроса	
function start(){
	document.getElementById('load').innerHTML = main;
	var k = param("r");	getAjax('itkl.php?s='+encodeURIComponent(k), itk);
}//Аякс запрос в базу данных
function itk(d){
	var k = param('r').split('`');//alert(k[2]);
	
	if(k[2].indexOf('1') > -1){itk1(d)}else {document.getElementById('kifa1').innerHTML = '';}
	if(k[2].indexOf('2') > -1){itk2(d)}else {document.getElementById('kifa2').innerHTML = '';}
	if(k[2].indexOf('3') > -1){itk3(d)}else {document.getElementById('kifa3').innerHTML = '';}
	if(k[2].indexOf('4') > -1){itk4(d)}else {document.getElementById('kifa4').innerHTML = '';}
	if(k[2].indexOf('5') > -1){itk5(d)}else {document.getElementById('kifa5').innerHTML = '';}
	
	if(k[2] == ''){document.getElementById('kifa1').innerHTML = '<p>Для распечатки технологии необходимо выбрать формы для отображения в информационно - консультативной службе "Технологии"</p>';}
	
