function itk1(d){
	var a = d.split('|'); var b = a[2].split('!'); var c; var R; var r; var e = a[1].split('$'); e2 = e[2].split('!');
	document.getElementById('itp1').innerHTML = 'Интегрированная технологическая карта.  '+e[1]+'.';
	document.getElementById('i1').rows[0].cells[0].innerHTML = 'Сорт:  '+e2[2]+'.  Площадь возделывания:  '+e[3]+'  га.  Шифр технологии:  '+e[0];
	for(var i = 0; i < b.length; i++){c = b[i].split('$'); 
		var s = c.join(); s = s.split('undefined'); s = s.join();
		if(s.length > 1){
			add1();
			R = document.getElementById('i1').rows;	r = R[R.length-1];
			r.cells[0].innerHTML = R.length-3;
